import 'package:flutter/material.dart';

class Portfolio extends StatefulWidget {
  const Portfolio({super.key});

  @override
  State<Portfolio> createState() => _PortfolioState();
}

class _PortfolioState extends State<Portfolio> {
  int _count = 0;
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text(
          "PORTFOLIO",
          style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
        ),
        backgroundColor: const Color.fromARGB(255, 213, 186, 194),
      ),
      backgroundColor: const Color.fromARGB(255, 235, 229, 231),
      body: SizedBox(
        width: double.infinity,
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            if (_count >= 1)
              const Text(
                "Name : Ankita Chougule",
                style: TextStyle(fontSize: 17, fontWeight: FontWeight.bold),
              ),
            const SizedBox(
              height: 10,
            ),
            if (_count >= 2)
              Image.asset(
                "assets/images/Ankita1.jpg",
                height: 150,
              ),
            const SizedBox(
              height: 40,
            ),
            if (_count >= 3)
              const Text(
                "College: Sinhgad Institute of Technology and Science",
                style: TextStyle(fontSize: 17, fontWeight: FontWeight.bold),
              ),
            const SizedBox(
              height: 10,
            ),
            if (_count >= 4)
              Image.network(
                  "data:image/jpeg;base64,"),
            const SizedBox(
              height: 40,
            ),
            if (_count >= 5)
              const Text(
                "Dream Company:Company",
                style: TextStyle(fontSize: 17, fontWeight: FontWeight.bold),
              ),
            const SizedBox(
              height: 10,
            ),
            if (_count >= 6)
              Image.network(
                  "data:image/jpeg;base64,"),
          ],
        ),
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {
          setState(() {
            _count++;
          });
        },
        backgroundColor: const Color.fromARGB(255, 213, 186, 194),
        child: const Text(
          "Next",
          style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
        ),
      ),
    );
  }
}
