import 'package:flutter/material.dart';

class DisplayImage extends StatefulWidget {
  const DisplayImage({super.key});
  @override
  State<DisplayImage> createState() {
    return _DisplayImageState();
  }
}

class _DisplayImageState extends State<DisplayImage> {
  List<String> imageList = ["data:image/jpeg;base64,",
"data:image/jpeg;base64,",
"data:image/jpeg;base64,"];
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text("Display Image"),
      ),
      body: ListView.builder(itemCount: imageList.length,
        itemBuilder: (context, index) {
        return Container(
          margin: const EdgeInsets.all(10),
          child: Image.network(imageList[index]),
        );
      }),
    );
  }
}
